// MODULES //

import Radium from 'radium';
import React from 'react';
import ReactDOM from 'react-dom';
import styles from 'styles';
import ReactCSSTransitionGroup from 'react-addons-css-transition-group';


// TOOLTIP //

class Tooltip extends React.Component {

	constructor( props ) {
		super( props );
	}

	componentDidMount() {
		let elem = ReactDOM.findDOMNode( this )
		elem.style.opacity = 0;
		window.requestAnimationFrame( () => {
			// Set a transition on the opacity:
			elem.style.transition = 'opacity 250ms';
			elem.style.opacity = 1;
		});
	}

	render() {
		return (
			<div className="tooltip">
				<ReactCSSTransitionGroup transitionName="default" transitionEnterTimeout={500} transitionLeaveTimeout={300}>
					<div key="tooltip" style={ [ styles.tooltip, { left: this.props.left, right: this.props.right } ] }>
					{this.props.text}
					</div>
				</ReactCSSTransitionGroup>
			</div>
		);
	}
}


// EXPORTS //

export default Radium( Tooltip );
